+		if colCount%3 == 0 {
+			out += "<gno-columns>\n"
+		}
+		post := item.Value.(*Post)
+		out += post.RenderPreview(gridMode, timeFmt, b.Prefix())
+		colCount++
+		if colCount%3 == 0 {
+			out += "</gno-columns>\n"
+		} else if gridMode {
+			out += "|||\n"
+		}
+	}
+	if colCount%3 != 0 {
+		out += "</gno-columns>\n"
+	}
+	return out
+}
